import { api } from "./Intercepter"
import userInfoService from "../services/userInfo.service";
export default {
  getBook : async (id) => {
    const books = await api.get(`books/${id}`);
    return books.data.data;

  },
  getUserBook : async (id) => {
      const books = await api.get(`books/user-book/${id}`);
      return books.data.data;
  
    },
    
    acceptThesis(id) {
    const response = api.post("/thesises/audit", {
      id: id,
      status: 'review',
      auditor_id:userInfoService.getUser().id
    })  
    return response;
  },
  rejectThesis(id,note) {
    const response = api.post("/thesises/audit", {
      id: id,
      status: 'rejected',
      auditor_id:userInfoService.getUser().id,
      reviews:note
    })  
    return response;
  },
  addDegree(id,note,mark){
    const response = api.patch(`/thesises/add-degree/${id}`, {
      reviewer_id:userInfoService.getUser().id,
      reviews:note,
      degree:mark
    })  
    return response;

  },
}
