import { api } from "../Intercepter"


export default {
    acceptThesis(id) {
        api.post("/thesises/audit-thesis", {
            id: id,
            status: 'review',
            auditor_id: userInfoService.getUser().id
        }).then(function (response) {
            console.log(response);
            return response;

        })
            .catch(function (error) {
                console.log(error);
                return error;

            });


    }

}
