import { api } from "./Intercepter"
import userInfoService from "../services/userInfo.service";
export default {
  addQuestion: async (question, quotes, userbook_id) => {
    let formData = new FormData();
    formData.append("question", question.question);
    formData.append("ending_page", question.pageEnd);
    formData.append("starting_page", question.pageStart);
    formData.append("user_book_id", userbook_id);
    formData.append("quotes", quotes);

    const res = await api
      .post("questions", formData)
      .catch((e) => {
        // error = e.response.data.message;
        console.log(e)
      }).then(e => console.log(e));
    // return {data:res.data,error}
  },
  getQuestions: async (id) => {

    const questions = await api.get(`questions/user-book/${id}`);

    return questions.data.data;
  },

  addDegree(id, note, mark) {
    const response = api.patch(`/questions/add-degree/${id}`, {
      reviewer_id: userInfoService.getUser().id,
      reviews: note,
      degree: mark
    })
    return response;

  },
  deleteQuestion(id) {
    const response = api.delete(`/questions/${id}`)
    return response;
  },
  getByStatus: async (status) => {

    const response = await api.get(`questions/status/${status}`);
    (response.data)
    return response.data.data;
  },
  getByUserBook: async (user_book_id) => {

    const response = await api.get(`questions/user_book_id/${user_book_id}`);
    (response.data)
    return response.data.data;
  },
  acceptQuestion(id) {
    const response = api.post("/questions/audit", {
      id: id,
      status: 'review',
      auditor_id: userInfoService.getUser().id
    })
    return response;
  },
  rejectQuestion(id, note) {
    const response = api.post("/questions/audit", {
      id: id,
      status: 'rejected',
      auditor_id: userInfoService.getUser().id,
      reviews: note
    })
    return response;
  },
  addDegree(id, note, mark) {
    const response = api.patch(`/questions/add-degree/${id}`, {
      reviewer_id: userInfoService.getUser().id,
      reviews: note,
      degree: mark
    })
    return response;

  },

}
