import { api } from "./Intercepter"
import userInfoService from "../services/userInfo.service";
export default {
  createThesis: async (thesis, userbook_id) => {

    let formData = new FormData();
    formData.append("thesis_text", thesis.text);
    formData.append("ending_page", thesis.pageEnd);
    formData.append("starting_page", thesis.pageStart);
    formData.append("user_book_id", userbook_id);
    formData.append("image", thesis.file[0]);
    const response = api.post("/thesises", formData, {
      headers: {
        "Content-Type": "multipart/form-data",
      },
    }).catch(error => {

      console.log(error)
    }
    )
    return response.data.data;
  },
  auditThesis: async (id) => {
    const res = await api
      .patch(`/thesises/audit-thesis/${id}`)
      .catch((e) => {
        // error = e.response.data.message;
        (e)
      }).then(e => (e));
    // return {data:res.data,error}
  },
  acceptThesis(id) {
    const response = api.post("/thesises/audit", {
      id: id,
      status: 'review',
      auditor_id: userInfoService.getUser().id
    })
    return response;
  },
  rejectThesis(id, note) {
    const response = api.post("/thesises/audit", {
      id: id,
      status: 'rejected',
      auditor_id: userInfoService.getUser().id,
      reviews: note
    })
    return response;
  },
  addDegree(id, note, mark) {
    const response = api.patch(`/thesises/add-degree/${id}`, {
      reviewer_id: userInfoService.getUser().id,
      reviews: note,
      degree: mark
    })
    return response;

  },
}
