import { api } from "./Intercepter";
import userInfoService from "../services/userInfo.service";
export default {
  checkCertificate: async (id) => {
    const res = await api.get(`userbook/certificate/${id}`);

    return res.data.data;
  },
  requestCertificateApi: async (id) => {
    try {
      const res = await api.post("userbook", { book_id: id });
      return res;
    } catch (e) {
      console.log(e);
    }
  },
};
